import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getMonthlyEvents } from "../../../actions/calendarActions";

const MonthlyEventsHandler = (id, month, year) => {
  const dispatch = useDispatch();
  const [calendarData, setCalendarData] = useState({});

  useEffect(() => {
    if ((id, month, year)) {
      dispatch(getMonthlyEvents(id, month, year));
    }
  }, [dispatch, id, month, year]);

  var profile = useSelector((userReducer) => userReducer.users.userProfile);

  useEffect(() => {
    if (profile) {
      setCalendarData(profile.data);
    }
  }, [profile]);

  return calendarData;
};

export default MonthlyEventsHandler;
