import React, { useState, useEffect } from "react";
import { Col, Row, Card, Form, FloatingLabel, Button } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { createNewEvent } from "../actions/eventActions";
import ReactQuill from "react-quill";
import Loading from "./Loading";
import ErrorToast from "./ErrorToast";

function CreatePost() {
  const dispatch = useDispatch();
  const [title, setTitle] = useState({});
  const [maxMembers, setMaxMembers] = useState({});
  const [details, setDetails] = useState({});
  const [load, setLoad] = useState(false);
  const [show, setShow] = useState(false);
  const [error, setError] = useState("");

  var s = '<div id="myDiv"></div>';
  var htmlObject = document.createElement('div');
  htmlObject.innerHTML = s;
  console.log(s)


  const handleSubmit = async (e) => {
    e.preventDefault();

    const postData = {
      title: title,
      maxMember: maxMembers,
      details: details,
    };

    dispatch(createNewEvent(postData, setLoad, setShow, setError));
  };

  return (
    <div className="mb-4">
      <Form
        onSubmit={(e) => {
          handleSubmit(e);
        }}
      >
        <Form.Group className="mb-3">
          <FloatingLabel
            controlId="floatingInput"
            label="Event Title"
            className="mb-3"
          >
            <Form.Control
              type="title"
              placeholder="Event Title"
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </FloatingLabel>
        </Form.Group>

        <Form.Group className="mb-3">
          <FloatingLabel
            controlId="floatingInput2"
            label="Maximum Members"
            className="mb-3"
          >
            <Form.Control
              type="number"
              placeholder="Maximum Members"
              onChange={(e) => setMaxMembers(parseInt(e.target.value))}
              required
            />
          </FloatingLabel>
        </Form.Group>
        <ReactQuill theme="snow" value={details} onChange={setDetails}/>
        <div className="centerContent align-items-center">
          <Button type="submit" className="mb-2 mt-3 w-100">
            {(() => {
              if (load) {
                return <Loading />;
              } else {
                return <>Post</>;
              }
            })()}
          </Button>
        </div>
      </Form>
    </div>
  );
}

export default CreatePost;
