import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getMonthlyEvents } from "../../../actions/calendarActions";

const MonthlyEventsHandler = (id, month, year) => {
  const dispatch = useDispatch();
  const [monthlyEventsData, setMonthlyEventsData] = useState([]);

  console.log(id);
  useEffect(() => {
    if ((id, month, year)) {
      dispatch(getMonthlyEvents(1, month, year));
    }
  }, [dispatch, id, month, year]);

  var calendarData = useSelector(
    (calendarReducer) => calendarReducer.calendarReducer.monthly
  );

  useEffect(() => {
    if (calendarData) {
      setMonthlyEventsData(calendarData.data);
    }
  }, [calendarData]);

  return monthlyEventsData;
};

export default MonthlyEventsHandler;
