import React, { useState, useEffect } from "react";
import { Card, Form, Row, Button, FloatingLabel } from "react-bootstrap";
import Footer from "./Footer";
import { useDispatch, useSelector } from "react-redux";
import { createNewUser } from "../actions/securityActions";
import Loading from "./Loading";
import {ErrorToast, SuccessToasts} from "./ErrorToast";

function SignUp() {
  const dispatch = useDispatch();
  const [username, setUsername] = useState({});
  const [email, setEmail] = useState({});
  const [password, setPassword] = useState({});
  const [load, setLoad] = useState(false);
  const [show, setShow] = useState(false);
  const [error, setError] = useState("");
  const [signUpStatus, setSignUpStatus] = useState(false);


  const handleSubmit = async (e) => {
    e.preventDefault();

    const userData = {
      name: username,
      email: email,
      password: password,
    };

    dispatch(createNewUser(userData, setLoad, setShow, setError));
  };

  var newUserStatus = useSelector((securityReducer) => securityReducer.security.userData);
  useEffect(() => {
    if(newUserStatus != null){
        setSignUpStatus(newUserStatus.success)
    }
  }, [newUserStatus]);


  return (
    <div className="fullPage">

      {signUpStatus ==true ? (<><SuccessToasts errors={error} showToast={show} /></>):(<><ErrorToast errors={error} showToast={show} /></>)}

      <Row className="centerContent p-3 login-card fullBody">
        <Card>
          <h3 className="centerContent mt-3">Create an account</h3>
          <Form
            className="p-4"
            onSubmit={(e) => {
              handleSubmit(e);
            }}
          >
            <Form.Label className="text-muted">Username</Form.Label>
            <Form.Group className="mb-3" controlId="formBasicEmail">
              <FloatingLabel
                controlId="floatingInput"
                label="Username"
                className="mb-3"
              >
                <Form.Control
                  type="text"
                  placeholder="mysticMac"
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </FloatingLabel>
            </Form.Group>

            <Form.Label className="text-muted">Email</Form.Label>
            <Form.Group className="mb-3" controlId="formBasicEmail2">
              <FloatingLabel
                controlId="floatingInput2"
                label="Email"
                className="mb-3"
              >
                <Form.Control
                  type="email"
                  placeholder="mysticMac"
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </FloatingLabel>
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
              <Form.Label className="text-muted">Password</Form.Label>
              <FloatingLabel controlId="floatingPassword" label="Password">
                <Form.Control
                  type="password"
                  placeholder="Password"
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </FloatingLabel>
            </Form.Group>

            <div className="centerContent align-items-center">
              <Button type="submit" className="mb-2 mt-3 w-100">
                {(() => {
                  if (load) {
                    return <Loading />;
                  } else {
                    return <>Continue</>;
                  }
                })()}
              </Button>
            </div>
          </Form>
          <Card.Footer >
            <small className="centerContent align-items-center">
               <a href="#">Already have an account?</a>
            </small>
          </Card.Footer>
        </Card>
      </Row>
      <Row className="footer-section">
        <Footer />
      </Row>
    </div>
  );
}

export default SignUp;
