import React, { useState, useEffect } from "react";
import { Row, Form, Button, Container, Modal } from "react-bootstrap";
import { useDispatch } from "react-redux";
import { getEventMembers, memberStatus } from "../../actions/eventActions";

function EventMembers(props) {
  const dispatch = useDispatch();
  const [members, setMembers] = useState([]);
  const [load, setLoad] = useState(false);
  const [modalShow, setModalShow] = useState(false);

  useEffect(() => {
    if (modalShow) {
      console.log(props.eventId);

      dispatch(getEventMembers(props.eventId, setMembers));
    }
  }, [modalShow]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const postData = {
        eventId: props.eventId,
        userId: members.userId,
        status: 2,
    };

    dispatch(memberStatus(postData, setLoad));
  };

  return (
    <div className="mb-4">
      <Button onClick={() => setModalShow(true)}>Members</Button>

      <Modal
        size="md"
        show={modalShow}
        onHide={() => setModalShow(false)}
        aria-labelledby="example-modal-sizes-title-lg"
      >
        <Modal.Header closeButton>
          <Modal.Title id="example-modal-sizes-title-lg">
            All members
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form   onSubmit={(e) => {
              handleSubmit(e);
            }}>
            <Container className="new-post-container p-3">
              <Row>
                {members.map((member) => (
                  <div key={member.id}>
                    <p>User Id: {member.userId}</p>
                  </div>
                ))}
              </Row>

              <Button type="submit" className="mb-2 mt-3 w-25">
                  {(() => {
                    if (load) {
                      return <Loading />;
                    } else {
                      return <>Confirm</>;
                    }
                  })()}
                </Button>
              <div className="d-flex justify-content-center"></div>
            </Container>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default EventMembers;
