import React, { useState, useEffect } from "react";
import { Row, Form, Button, Container, Modal } from "react-bootstrap";
import { useDispatch } from "react-redux";
import { getEventMembers } from "../../actions/eventActions";

function EventMembers(props) {
  const dispatch = useDispatch();
  const [members, setMembers] = useState([]);
  const [modalShow, setModalShow] = useState(false);

  useEffect(() => {
    if (modalShow) {
      console.log(props.eventId);

      dispatch(getEventMembers(props.eventId, setMembers));
    }
  }, [modalShow]);



  return (
    <div className="mb-4">
      <Button className="me-2" onClick={() => setModalShow(true)}>Members</Button>

      <Modal
        size="lg"
        show={modalShow}
        onHide={() => setModalShow(false)}
        aria-labelledby="example-modal-sizes-title-lg"
      >
        <Modal.Header closeButton>
          <Modal.Title id="example-modal-sizes-title-lg">
            All members
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Container className="new-post-container p-3">
              <Row>
                {members.map((member) => (
                  <div key={member.id}>
                    <p>User Id: {member.userId}</p>
                  </div>
                ))}
              </Row>

              <div className="d-flex justify-content-center"></div>
            </Container>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default EventMembers;
