import React from "react";
import { Badge } from "react-bootstrap";

function Days(props) {
  const firstCalendarDay = new Date(
    props.day.getFullYear(),
    props.day.getMonth(),
    1
  );

  const indexOfFIrstDay = firstCalendarDay.getDay();

  let currentCalendarDays = [];

  for (let calendarIndex = 0; calendarIndex < 42; calendarIndex++) {
    if (calendarIndex === 0 && indexOfFIrstDay === 0) {
      firstCalendarDay.setDate(firstCalendarDay.getDate() - 7);
    }
    //set day on the first index  of the calendar
    else if (calendarIndex === 0) {
      console.log(firstCalendarDay);
      firstCalendarDay.setDate(
        firstCalendarDay.getDate() + (calendarIndex - indexOfFIrstDay)
      );
    } else {
      firstCalendarDay.setDate(firstCalendarDay.getDate() + 1);
    }

    let calendarDay = {
      isCurrMonth: firstCalendarDay.getMonth() === props.day.getMonth(),
      fullDate: new Date(firstCalendarDay),
      month: firstCalendarDay.getMonth(),
      day: firstCalendarDay.getDate(),
      selected: firstCalendarDay.toDateString() === props.day.toDateString(),
      year: firstCalendarDay.getFullYear(),
    };

    currentCalendarDays.push(calendarDay);
  }

  return (
    <div className="calendar-days">
      {currentCalendarDays.map((days, index) => {
        return (
          <div
            key={index}
            className={`day ${days.isCurrMonth ? `currMonth` : ``} ${
              days.selected ? `selectedDay` : ``
            }`}
            onClick={() => props.changeCurrentDay(days)}
          >
            <p>{days.day}</p>
            {props.eventIndexes
              ? props.eventIndexes.map((data, index) => {
                  return data.day === days.day &&
                    props.currentMonth === days.month ? (
                    <Badge key={index}>{data.count}</Badge>
                  ) : (
                    ""
                  );
                })
              : null}
          </div>
        );
      })}
    </div>
  );
}

export default Days;
