import React, { useState } from "react";
import { Col, Row, Card } from "react-bootstrap";
import img from "../logo192.png";

function PostItem(props) {

  var parser = new DOMParser();
	var doc = parser.parseFromString(props.eventDetails, 'text/html');
  let data = doc.paragraph
  const htmlSections = doc.childNodes[0].childNodes;

  let sections = Object.keys(htmlSections).map((key, i) => {
    let el = htmlSections[key];
    let contents = [<p>{el.innerHTML}</p>];



  });




  return (
    <div >
      {/* <Row className="row justify-content-center">Event Data</Row> */}
   {sections}
          <Card>
            <Card.Body>
              <Card.Header>
                {" "}
                <Row xs="auto">
                  <Col>
                    <img src={img} className="jobCard-Logo"></img>
                  </Col>
                  <Col>
                    <h6 className="fw-bold">{props.eventTitle}</h6>

                    <small
                      className="text-muted"
                      style={{ fontSize: "12px", display: "block" }}
                    >
                      @{props.postedBy} . 5 days ago
                    </small>
                  </Col>
                </Row>
              </Card.Header>
              <Card.Text className="mt-4">

                {props.eventDetails}
              </Card.Text>
            </Card.Body>
          </Card>
          <hr />
    </div>
  );
}

export default PostItem;
