import axios from "axios";
import { GET_ALL_EVENTS,  GET_ERRORS } from "./types";
import jwt_decode from "jwt-decode";
import { isExpired } from "react-jwt";
import setToken from "./securityUtils/setToken";


const token = localStorage.token;
const accessToken = localStorage.accessToken

const isMyTokenExpired = isExpired(accessToken);
console.log(isMyTokenExpired)

const getAccessToken = async (token) => {
  try {
    const refToken = {
      refresh: token,
    };
    const res = await axios.post(
      "http://localhost:8000/api/auth/token/refresh/",
      refToken
    );
    setToken(res.data.access);
    const decoded_token = jwt_decode(res.data.access);
    store.dispatch({
      type: SET_CURRENT_USER,
      payload: decoded_token,
    });
  } catch (error) {
    store.dispatch({
      type: GET_ERRORS,
      payload: error.message,
    });
  }
};

export const getAllEvents = () => async (dispatch) => {
  if (token && isMyTokenExpired ) {
    console.log("here")
    getAccessToken(token);
  }
  const res = await axios.get(`http://localhost:8000/api/event/all/`);
  dispatch({
    type: GET_ALL_EVENTS,
    payload: res.data,
  });
};

export const createNewEvent = (postData, setLoad, setShow, setError) => async (dispatch) => {
  try {
    setLoad(true);
    if (token && isMyTokenExpired ) {
      getAccessToken(token);
    }
    const res = await axios.post("http://localhost:8000/api/event/create/", postData);
    console.log(res)
    if(res.data.success == "true"){
      setLoad(false)
      setShow(true)
      setError(res.data.message);
      window.location.reload();
    }
    dispatch({
      type: GET_ERRORS,
      payload: res.data,
    });

  } catch (error) {
    setLoad(false);
    setShow(true);
    setError(error.response.data.message);
    dispatch({
      type: GET_ERRORS,
      payload: error.response.data,
    });
  }
};
