import axios from "axios";
import {
  SET_CURRENT_USER,
  GET_USERS,
  GET_USER,
} from "./types";
import jwt_decode from "jwt-decode";



export const getUsers = () => async (dispatch) => {
  const res = await axios.get(`http://localhost:8000/api/users/all/`);
  console.log(res)
  dispatch({
    type: GET_USERS,
    payload: res.data,
  });
};

export const getUser = (id) => async (dispatch) => {
  const res = await axios.get(`http://localhost:8000/api/user/${id}`);
  dispatch({
    type: GET_USER,
    payload: res.data,
  });
};
