import React, { useState } from "react";
import { Button, Table } from "react-bootstrap";
import { ChevronLeft, ChevronRight } from "../SiteElements/icons";
import Days from "./days";
import {
  DayEventsHandler,
  MonthlyEventsHandler,
} from "./utilities/action-handler";

function Calendar(props) {
  // eslint-disable-next-line no-unused-vars
  const [days, setDays] = useState([
    "Sun",
    "Mon",
    "Tue",
    "Wed",
    "Thu",
    "Fri",
    "Sat",
  ]);

  // eslint-disable-next-line no-unused-vars
  const [months, setMonths] = useState([
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ]);

  const [currentDay, setCurrentDay] = useState(new Date());

  const changeCurrentDay = (day) => {
    setCurrentDay(new Date(day.year, day.month, day.day));
  };

  const nextMonth = () => {
    setCurrentDay(new Date(currentDay.setMonth(currentDay.getMonth() + 1)));
  };

  const prevMonth = () => {
    setCurrentDay(new Date(currentDay.setMonth(currentDay.getMonth() - 1)));
  };

  const monthlyEvents = MonthlyEventsHandler(
    props.userId,
    currentDay.getMonth() + 1,
    currentDay.getFullYear()
  );

  if (monthlyEvents) {
    var indexes = [];

    for (var i = 0; i < monthlyEvents.length; i++) {
      if (monthlyEvents[i] > 0) {
        indexes.push({ day: i + 1, count: monthlyEvents[i] });
      }
    }
  }

  // eslint-disable-next-line no-unused-vars
  const dayEvents = DayEventsHandler(
    props.userId,
    currentDay.getDate(),
    currentDay.getMonth() + 1,
    currentDay.getFullYear()
  );

  return (
    <div className="calendar">
      <div>
        {dayEvents
          ? dayEvents.map((item, i) => {
              return (
                <span key={i}>
                  {item.title}
                  <br />
                </span>
              );
            })
          : null}
      </div>

      <div className="head">
        {currentDay.getFullYear()}
        <Button onClick={prevMonth}>
          <ChevronLeft />
        </Button>
        <h6 className="m-0">{months[currentDay.getMonth()]}</h6>

        <Button onClick={nextMonth}>
          <ChevronRight />
        </Button>
      </div>

      {/*    <div className="body">
        <div className="header">
          {days.map((i, index) => {
            return (
              <div key={index} className="week">
                {i}
              </div>
            );
          })}
        </div>

        <Days
          day={currentDay}
          changeCurrentDay={changeCurrentDay}
          eventIndexes={indexes}
          currentMonth={currentDay.getMonth()}
        />
      </div> */}

      <Table responsive>
        <thead>
          <tr>
            {days.map((i, index) => {
              return (
                <th key={index} className="week">
                  {i}
                </th>
              );
            })}
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>1</td>
            {Array.from({ length: 12 }).map((_, index) => (
              <td key={index}>Table cell {index}</td>
            ))}
          </tr>
          <tr>
            <td>2</td>
            {Array.from({ length: 12 }).map((_, index) => (
              <td key={index}>Table cell {index}</td>
            ))}
          </tr>
          <tr>
            <td>3</td>
            {Array.from({ length: 12 }).map((_, index) => (
              <td key={index}>Table cell {index}</td>
            ))}
          </tr>
        </tbody>
      </Table>
    </div>
  );
}

export default Calendar;
