import React, { useState } from "react";
import { Toast } from "react-bootstrap";

function ErrorToast(props) {
  let errorT = props.showToast;
  const [toast, setToast] = useState(props.showToast);

  return (
    <div xs={6}>
      <Toast
        // className="toast position-absolute mt-4"
        style={{ background: "#FFD2D2", color: "#D8000C", fontSize: "18px" }}
        onClose={() => setToast(toast)}
        show={toast}
        delay={1000}
        autohide
      >
        <Toast.Body>{props.errors}</Toast.Body>
      </Toast>
    </div>
  );
}

export default ErrorToast;
