import { GET_ALL_EVENTS, GET_EVENT_MEMBERS } from "../actions/types";

const initialState = {
  events: {},
  data: [],
  eventMembers: [],
};

export default function setEvents(state = initialState, action) {
  switch (action.type) {
    case GET_ALL_EVENTS:
      return {
        ...state,
        events: action.payload,
        data: action.payload.data,
      };
    case GET_EVENT_MEMBERS:
      return {
        ...state,
        eventMembers: action.payload,
        data: action.payload.data,
      };
    default:
      return state;
  }
}
