import React, { useState, useEffect } from "react";
import { Col, Row, Card, Form, FloatingLabel } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { createNewEvent } from "../actions/eventActions";
import ReactQuill from "react-quill";

function CreatePost() {
  const dispatch = useDispatch();
  const [title, setTitle] = useState({});
  const [maxMembers, setMaxMembers] = useState({});
  const [details, setDetails] = useState({});
  const [load, setLoad] = useState(false);
  const [show, setShow] = useState(false);
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    const postData = {
      title: title,
      maxMembers: maxMembers,
      details: details,
    };

    dispatch(createNewEvent(postData, setLoad, setShow, setError));
  };

  return (
    <div className="mb-4">
      <Form
        onSubmit={(e) => {
          handleSubmit(e);
        }}
      >
            <Form.Group className="mb-3">
              <FloatingLabel
                controlId="floatingInput"
                label="Event Title"
                className="mb-3"
              >
                <Form.Control
                  type="title"
                  placeholder="Event Title"
                  onChange={(e) => setTitle(e.target.value)}
                  required
                />
              </FloatingLabel>
            </Form.Group>

            <Form.Group className="mb-3">
              <FloatingLabel
                controlId="floatingInput2"
                label="Maximum Members"
                className="mb-3"
              >
                <Form.Control
                  type="number"
                  placeholder="Maximum Members"
                  onChange={(e) => maxMembers(e.target.value)}
                  required
                />
              </FloatingLabel>
            </Form.Group>
        <ReactQuill onChange={(e) => setDetails(e.target.value)} />
      </Form>
    </div>
  );
}

export default CreatePost;
