import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getMonthlyEvents } from "../../../actions/calendarActions";

const MonthlyEventsHandler = (id, month, year) => {
  const dispatch = useDispatch();
  const [monthlyEventsData, setMonthlyEventsData] = useState({});

  useEffect(() => {
    if ((id, month, year)) {
      dispatch(getMonthlyEvents(id, month, year));
    }
  }, [dispatch, id, month, year]);

  var calendarData = useSelector((calendarReducer) => calendarReducer.monthly);

  useEffect(() => {
    if (calendarData) {
      console.log(calendarData);

      setMonthlyEventsData(calendarData.data);
    }
  }, [calendarData]);

  return monthlyEventsData;
};

export default MonthlyEventsHandler;
