import React from "react";
import { Badge } from "react-bootstrap";

function Days(props) {
  const lastCalendarDay = new Date(
    props.day.getFullYear(),
    props.day.getMonth(),
    1
  );

  const indexOfFIrstDay = lastCalendarDay.getDay();
  let currentDays = [];

  for (let day = 0; day < 42; day++) {
    if (day === 0 && indexOfFIrstDay === 0) {
      lastCalendarDay.setDate(lastCalendarDay.getDate() - 7);
    } else if (day === 0) {
      lastCalendarDay.setDate(
        lastCalendarDay.getDate() + (day - indexOfFIrstDay)
      );
    } else {
      lastCalendarDay.setDate(lastCalendarDay.getDate() + 1);
    }

    let calendarDay = {
      isCurrMonth: lastCalendarDay.getMonth() === props.day.getMonth(),
      fullDate: new Date(lastCalendarDay),
      month: lastCalendarDay.getMonth(),
      day: lastCalendarDay.getDate(),
      selected: lastCalendarDay.toDateString() === props.day.toDateString(),
      year: lastCalendarDay.getFullYear(),
    };

    currentDays.push(calendarDay);
  }

  console.log(llastCalendarDay);
  return (
    <div className="calendar-days">
      {currentDays.map((days, index) => {
        return (
          <div
            key={index}
            className={`day ${days.isCurrMonth ? `currMonth` : ``} ${
              days.selected ? `selectedDay` : ``
            }`}
            onClick={() => props.changeCurrentDay(days)}
          >
            <p>{days.day}</p>
            {props.eventIndexes
              ? props.eventIndexes.map((data, index) => {
                  return data.day === days.day &&
                    props.currentMonth === days.month ? (
                    <Badge key={index}>{data.count}</Badge>
                  ) : (
                    ""
                  );
                })
              : null}
          </div>
        );
      })}
    </div>
  );
}

export default Days;
