import React, { useState, useEffect } from "react";
import { Row, Form, Button, Container, Modal } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { getEventMembers } from "../../actions/eventActions";
import Loading from "../SiteElements/Loading";

function EventMembers(props) {
  const dispatch = useDispatch();
  const [load, setLoad] = useState(false);
  const [error, setError] = useState("");
  const [modalShow, setModalShow] = useState(false);

  useEffect(() => {
    if (modalShow) {
      dispatch(getEventMembers(props.eventId));
    }
  }, []);

//   var allEventsData = useSelector((eventReducer) => eventReducer.events.data);
  /*  useEffect(() => {
    setEventData(allEventsData);
  }, [allEventsData]); */

//   console.log(allEventsData);

  return (
    <div className="mb-4">
      <Button onClick={() => setModalShow(true)}>Members</Button>

      <Modal
        size="lg"
        show={modalShow}
        onHide={() => setModalShow(false)}
        aria-labelledby="example-modal-sizes-title-lg"
      >
        <Modal.Header closeButton>
          <Modal.Title id="example-modal-sizes-title-lg">
            All members
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Container className="new-post-container p-3">
              <Row>{/* <p>Send request to join {props.eventTitle}.</p> */}</Row>

              {error}
              <div className="d-flex justify-content-center"></div>
            </Container>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default EventMembers;
